import React, { useState, useEffect } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";
import Login from "./Login";

function App() {

  const [loggedIn, setLoggedIn] = useState(localStorage.getItem("loggedIn") || false);
  const [user, setUser] = useState(null);
  const [currentUser, setCurrentUser] = useState(localStorage.getItem("currentUser") || "");

  useEffect(() => {
    fetch("/user")
    .then((res) => res.json())
    .then((user) => {setUser(user.credentials)})
  }, []);

  function checkLogin(credentials){
    user.forEach((element)=>{
      if(element.username === credentials.username && element.password === credentials.password){
        setLoggedIn(true);
        localStorage.setItem("loggedIn", true);
        setCurrentUser(element._id);
        localStorage.setItem("currentUser", element._id);
      }
    });
  }

  function logout(){
    setLoggedIn(false);
    localStorage.setItem("loggedIn", false);
    setCurrentUser("");
    localStorage.setItem("currentUser", "");
  }



  const [notes, setNotes] = useState([]);
  useEffect(() =>{
    fetch("/notes/" + currentUser)
    .then((res) => {
      return res.json()
    })
    .then((notes) => {
      // console.log(notes.notes);
      setNotes(prevValue => {
        return [...prevValue, ...notes.notes]
      })
    })  
  })

  function addNote(newNote) {
    setNotes(prevNotes => {
      return [...prevNotes, newNote];
    });
    console.log(newNote);
    newNote.id = currentUser;

    fetch("/create", {
      method: 'POST',
      headers:{
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newNote)
    }) 
  }

  function deleteNote(id) {
    setNotes(prevNotes => {
      return prevNotes.filter((noteItem, index) => {
        return index !== id;
      });
    });
  }
  if(currentUser !== ""){
    return (
      <div>
        <Header clicked={logout} isLoggedIn={loggedIn}/>
        <CreateArea id={currentUser} onAdd={addNote} />
        {notes.map((noteItem, index) => {
          return (
            <Note
              key={index}
              id={index}
              title={noteItem.title}
              content={noteItem.content}
              onDelete={deleteNote}
            />
          );
        })}
        <Footer />
      </div>
    );
  }else{
    return (
      <div>
        <Header isLoggedIn={loggedIn}/>
        <Login onSubmitForm={checkLogin} />
        <Footer />
      </div>
    );
  }

}

export default App;
