import React, { useState, useEffect } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";
import Login from "./Login";
import Register from "./Register";

function App() {

  const [loggedIn, setLoggedIn] = useState((localStorage.getItem("loggedIn") !== "false") || false);
  const [registerWanted, setRegisterWanted] = useState(false);
  const [currentUser, setCurrentUser] = useState(localStorage.getItem("currentUser") || "");

  function registerClick(){
    setRegisterWanted((previousState) => !previousState);
  }

  function registerAccount(credentials){
    console.log(credentials);
    fetch('/register', )
  }


  function checkLogin(credentials){
    fetch("/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(credentials)
    })
    .then((res) => res.json())
    .then((user) => {
      if(user.success){
        setLoggedIn(true);
        setCurrentUser(user.userID);
        localStorage.setItem("loggedIn", true);
        localStorage.setItem("currentUser", user.userID);
        fetchNotes(user);
      }
    })
  }

  function fetchNotes(user){
    fetch("/notes/" + user.userID)
        .then((res) => {
          return res.json();
        })
        .then((result) => {
          setNotes(result.notes);
          return;
        })  
  }

  function logout(){
    setLoggedIn(false);
    localStorage.setItem("loggedIn", false);
    setCurrentUser("");
    localStorage.setItem("currentUser", "");
    setNotes([]);
  }



  const [notes, setNotes] = useState([]);

  useEffect(() =>{
    if(currentUser !== "" && currentUser !== "undefined"){
      fetch("/notes/" + currentUser)
      .then((res) => {
        return res.json();
      })
      .then((result) => {
        setNotes(result.notes);
        return;
      })
    }  
  }, [])

  function addNote(newNote) {
    setNotes(prevNotes => {
      return [...prevNotes, newNote];
    });
    console.log(newNote);
    newNote.id = currentUser;

    fetch("/create", {
      method: 'POST',
      headers:{
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newNote)
    }) 
  }

  function deleteNote(id) {
    setNotes(prevNotes => {
      return prevNotes.filter((noteItem, index) => {
        return index !== id;
      });
    });
  }

    if(currentUser !== "" && currentUser !== "undefined"){
      return (
        <div>
          <Header clicked={logout} isLoggedIn={loggedIn}/>
          <CreateArea id={currentUser} onAdd={addNote} />
          {notes.map((noteItem, index) => {
            return (
              <Note
                key={index}
                id={index}
                title={noteItem.title}
                content={noteItem.content}
                onDelete={deleteNote}
              />
            );
          })}
          <Footer />
        </div>
      );
    }
    else if(registerWanted){
      return <Register clicked={registerClick} onSubmitForm={registerAccount} isLoggedIn={loggedIn} />;
    }
    else{
      return (
        <div>
          <Header isLoggedIn={loggedIn}/>
          <Login onSubmitForm={checkLogin} clicked={registerClick} />
          <Footer />
        </div>
      );
    }

}

export default App;
